import { React, useState, useEffect } from 'react';
import { Link } from "react-router-dom"
//import WaterGlass from '../components/WaterGlass';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faBullseye, faTrophy } from '@fortawesome/free-solid-svg-icons'

function WaterPage() {

  const [time, setTime] = useState([]);
  const [reset, setReset] = useState(false);
  const [clickCount, setClickCount] = useState(0)
  const [seconds, setSeconds] = useState({
    sec1: 0,
    sec2: 0,
    secTotal: 60,
    zero: 0
  });
  const [minutes, setMinutes] = useState({
    min1: '',
    min2: '',
    minTotal: 0,
    zero: 0
  });

  //Time now to stop the countdown at the selected hour
  let today = new Date();
  let currentHour = today.getHours();

  //Get question page info from local storage. 
  useEffect(() => {
    const time = JSON.parse(localStorage.getItem('time'));
    if (time) {
      setTime(time)
    }
    setMinutes(minutes => ({
      ...minutes,
      minTotal: time.remindAmount
    }))
  }, []);

  //Initially sets the single minutes
  useEffect(() => {
    if (time.length !== 0) {
      let min = time.remindAmount - 1
      let newMin = min.toString().split('')
      let minSplit = newMin.map(Number)
      setMinutes(minutes => ({
        ...minutes,
        minTotal: minutes.minTotal - 1,
        min1: minSplit[0],
        min2: minSplit[1]
      }))
  }
  }, [time])


  //timer countdown.
  useEffect(() => {
    //Splits the seconds to individual seconds
    let sec = seconds.secTotal - 1
    let newSec = sec.toString().split('')
    let secSplit = newSec.map(Number)
    if (secSplit.length === 1) {
      secSplit.unshift(0)
    }
    //Splits the minutes to individual digits
    let min = minutes.minTotal - 1
    let newMin = min.toString().split('')
    let minSplit = newMin.map(Number)
    if (minSplit.length === 1) {
      secSplit.unshift(0)
    }
    //Greater than 0 - countdown keeps going
    if (seconds.secTotal > 0) {
      const timer = () => setTimeout(() => setSeconds(seconds => ({...seconds, secTotal: seconds.secTotal - 1, sec1: secSplit[0], sec2: secSplit[1]})), 1000);
      const timerId = timer()
      if (reset === true) {
        clearTimeout(timerId)
        setReset(false)
        setMinutes(minutes => ({
          ...minutes,
          minTotal: time.remindAmount - 1
        }))
        setSeconds(seconds => ({
          ...seconds,
          secTotal: 60
        }))
      }
    }
    //Seconds = 0 will reduce minutes by 1 and reset seconds to 59
    if(seconds.secTotal === 0 && reset === false) {
      setMinutes(minutes => ({
        ...minutes,
        minTotal: minutes.minTotal - 1,
        min1: minSplit[0],
        min2: minSplit[1]
      }))
      setSeconds(seconds => ({
        ...seconds,
        secTotal: 59
      }))
    }
  }, [seconds]);


  //Figure out how much water to drink each time
  let difference = time.endValue - time.startValue;
  let remind;
  switch (time.remindValue) {
    case 1:
      remind = 0.5;
      break;
    case 2: 
      remind = 0.75;
      break;
    case 3: 
      remind = 1;
      break;
    default:
      remind = 1.5;
  }
  let remindAmountofTimes = Math.floor(difference / remind);
  let drinkAmount = Math.round((time.waterAmount * 1000) / remindAmountofTimes);
  let totalDrank = drinkAmount * clickCount;

  const handleReset = () => {
    setReset(true)
    setClickCount(prevClick => {
      return ( prevClick + 1 )
    })
  }

  const timerRunning = () => {
    <div className="goal">
      <div className="icon"><FontAwesomeIcon icon={faBullseye} /> </div>
      <h2 className="sub-heading">Your Goal</h2>
      <p className="test-text">
        To drink <span className="drink-amount">{time.waterAmount}</span> litres in <span className="drink-amount">{difference}</span> hours.</p>
      <div className="icon"><FontAwesomeIcon icon={faTrophy} /></div>
      <h2 className="sub-heading">How to achieve</h2>
      <p className="test-text">Drink <span className="drink-amount">{drinkAmount}</span>  mls every time the countdown reaches 0.
        <br />Press the "I've drank water" button to reset the time<br />
      </p>
    </div>
  }

  const timerFinished = () => {
    let result;
    if (time.waterAmount * 1000 > totalDrank) {
       result = false
    }
    return (
      <div className="goal">
        <div className="icon"><FontAwesomeIcon icon={faBullseye} /> </div>
        <h2 className="sub-heading">Your Goal</h2>
        <p className="test-text">
          To drink <span className="drink-amount">{time.waterAmount}</span> litres in <span className="drink-amount">{difference}</span> hours.</p>
        <div className="icon"><FontAwesomeIcon icon={faTrophy} /></div>
        <h2 className="sub-heading">Your achievement today</h2>
        <p className="test-text">You drank <span className="drink-amount">{totalDrank}</span> in {difference} hours. {result ? <p>Congratulations, you reached your goal!</p> : <p>You didn't quite drink enough today, lets try again tomorrow</p>}
          <br />Press the "I've drank water" button to reset the time<br />
        </p>
      </div>
    )
  }

  return (
    <div className="App">
      <h1 className="drink-heading">Drink up!</h1>
      <div className="timer">
        <div className="timer-inner">
          <div className="min1 time">{minutes.min1}</div>
          <div className="min2 time">{minutes.min2}</div>
          <span className="colon time">:</span>
          <div className="sec1 time">{seconds.sec1}</div>
          <div className="sec2 time">{seconds.sec2}</div>
        </div>
      </div>
      {currentHour > time.endValue ? {timerFinished} : {timerRunning} }
      {/* <WaterGlass /> */}
      <button className="drink-button" onClick={handleReset}>I drank water! </button>
      <Link to="/"><button className="reset-button">Start again</button></Link>
      {currentHour > time.endValue ? <p>Stop drinking water! You can reset again tomorrow</p> : <p>Keep drinking!!</p>}

    </div>
  )
}

export default WaterPage;


//If the timer only resets every time you click the button, there should be a point at the end time
//when it stops calculating (ie.7pm) then displays how much water you drank based on the amount to drink each time
//and the amount of times you click the button. 
//Also - an alarm that goes off when the times reaches 0
//Fix the css of the countdown, and the buttons
//Sort out what happens when the timer reaches 0
//Water glass animation
