import { React, useState, useEffect } from 'react';
import { Link } from "react-router-dom"
import WaterGlass from '../components/WaterGlass';
//import Questions from './Questions';

function WaterPage() {

  const [time, setTime] = useState([]);
  const [countdown, setCountdown] = useState([]);

  let today = new Date();
  let currentTime = today.getHours() + ":" + today.getMinutes();
  let currentHour = today.getHours();

  useEffect(() => {
    const time = JSON.parse(localStorage.getItem('time'));
    if (time) {
     setTime(time);
    }
  }, []);

  useEffect(() => {
    const countdown = JSON.parse(localStorage.getItem('countdown'));
    if (countdown) {
     setCountdown(countdown);
    }
  }, []);

  const [counter, setCounter] = useState(60);
  const [minutes, setMinutes] = useState(29);

  useEffect(() => {
    counter > 0 && setTimeout(() => setCounter(counter - 1), 1000);
    if(counter === 0) {
      setMinutes(minutes-1)
      setCounter(59)
    }
    if (minutes === 0) {
      setMinutes(30)
    }

  }, [counter]);


  return (
    <>
      <h1 className="test-text">Amount of water to drink is {time.waterAmount}</h1>
      <h1>Time now is {currentTime}</h1>
      <h1>The countdown is {countdown}</h1>
      {currentHour > time.endValue ? <p>Stop drinking water! You can reset again tomorrow</p> : <p>Keep drinking!!</p>}
      <div className="timer">
      {counter < 10 ? <div>Countdown: {minutes}:0{counter}</div> : <div>Countdown: {minutes}:{counter}</div>} 
      </div>
      <WaterGlass />
      <button className="reset-button">I drank water!! </button>
      <Link to="/"><button>Reset</button></Link>
    </>
  )
}

export default WaterPage;


//If the timer only resets every time you click the button, there should be a point at the end time
//when it stops calculating (ie.7pm) then displays how much water you drank based on the amount to drink each time
//and the amount of times you click the button. 
//Also - an alarm that goes off when the times reaches 0
