import React from 'react';

function Slider(props) {

  return (
    <div className="slider">
      <input type="range" name="waterAmount" value={props.sliderValue} id="waterAmount" min="1" max="4" step="0.5" onChange={props.sliderChange}/>
      <output>{props.sliderValue}</output>
    </div>
  )
}

export default Slider;
