import { React, useState } from 'react';
import Dropdown from '../components/Dropdown';
import Slider from '../components/Slider';
import { startTimes, endTimes, remindTimes } from '../times';
import TestDropdown from '../components/testDropdown';


function Questions(props) {

  const [sliderValue, setSliderValue] = useState(2)
  //const [selected, setSelected] = useState("-")
  const [time, setTime] = useState({
    id: "",
    start: "12pm",
    end: "",
    startValue: 0,
    endValue: 0,
    remindTime: "",
    remindValue: 0,
    total: 0
  })

  const makeStartTimes = startTimes.map(startTime => {
    return (
      <option key={startTime.id} name={startTime.time} value={startTime.value}>{startTime.time}</option>
    )
  })



  const makeEndTimes = endTimes.map(endTime => {
    return (
      <option key={endTime.id} name={endTime.time} value={endTime.value}>{endTime.time}</option>
    )
  })

  const makeRemindTimes = remindTimes.map(remindTime => {
    return (
      <option key={remindTime.id} name={remindTime.time} value={remindTime.value}>{remindTime.time}</option>
    )
  })

  function handleStartSelect(event) {
    setTime(time => ({
      ...time,
      start: event.target[event.target.selectedIndex].label,
      startValue: event.target.value
    }));
  }

  function handleEndSelect(event) {
    setTime(time => ({
      ...time,
      end: event.target[event.target.selectedIndex].label,
      endValue: event.target.value,
      total: (time.endValue - time.startValue)
    }));
    console.log(time.endValue)
  }

  function handleRemindSelect(event) {
    //let difference = time.endValue
    setTime(time => ({
      ...time,
      remindTime: event.target[event.target.selectedIndex].label,
      remindValue: event.target.value,
    }))


  }

  function handleSlider(event) {
    setSliderValue(event.target.value)
  }



  return (
    <>
    <h2>Start drinking water at...</h2>
    <Dropdown options={makeStartTimes} change={handleStartSelect}/>

    <h2>Stop drinking water at ...</h2>
    <Dropdown options={makeEndTimes} change={handleEndSelect}/>
    <Slider sliderChange={handleSlider} sliderValue={sliderValue}/>
    <h2>Remind me every</h2>
    <Dropdown options={makeRemindTimes} change={handleRemindSelect} />
    {/* <Dropdown options={makeRemindTimes} selected={selected} onSelectedChange={setSelected}/> */}
    {/* Need a new state called selected */}
    <h2>Start drinking at {time.start} and finish drinking at {time.end}</h2>
    <h2>You want to drink {sliderValue} litres over {time.total} hours and be reminded every {time.remindTime}</h2>
    <button className="get-started" onClick={props.drinkClick}>Start drinking!</button>
    </>
  )
}

