import { React, useState, useEffect } from 'react';
import { Link } from "react-router-dom"
//import WaterGlass from '../components/WaterGlass';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faBullseye, faTrophy } from '@fortawesome/free-solid-svg-icons'

function WaterPage() {

  const [time, setTime] = useState([]);
  const [seconds, setSeconds] = useState([{
    sec1: 0,
    sec2: 0,
    secTotal: 60,
    zero: 0
  }]);
  const [minutes, setMinutes] = useState([{
    min1: 0,
    min2: 0,
    minTotal: 0,
    zero: 0
  }]);

  //Time now to stop the countdown at the selected hour
  let today = new Date();
  let currentHour = today.getHours();

  //Get question page info from local storage. 
  useEffect(() => {
    const time = JSON.parse(localStorage.getItem('time'));
    if (time) {
      setTime(time);
      setMinutes(minutes => ({
        ...minutes,
        minTotal: time.remindAmount - 1
      }))
    }
  }, []);

  //Splits minutes into single digits
  useEffect(() => {
    let minuteDigits = minutes.minTotal;
    let minuteString = minuteDigits.toString();
    let minuteSplit = minuteString.split('');
    let newMinute = minuteSplit.map(Number)
    setMinutes(minutes => ({
      ...minutes,
      min1: (minutes.minTotal < 10 ? minutes.zero : newMinute[0]),
      min2: newMinute[1]
    }))
  }, [minutes.minTotal]);


  //timer countdown. Conditional rendering to add an extra 0 to single digit minutes
  useEffect(() => {
    seconds.secTotal > 0 && setTimeout(() => setSeconds(seconds => ({...seconds, secTotal: seconds.secTotal - 1})), 1000);
    if(seconds.secTotal === 0) {
      setMinutes(prevMinute => ({
        ...minutes,
        min2: (prevMinute <= 10 ? minutes.zero + prevMinute - 1 : (prevMinute - 1))
      }))
      setSeconds(seconds => ({
        ...seconds,
        secTotal: 59
      }))
    }

  }, [seconds]);



  //Figure out how much water to drink each time
  let difference = time.endValue - time.startValue;
  let remind;
  switch (time.remindValue) {
    case 1:
      remind = 0.5;
      break;
    case 2: 
      remind = 0.75;
      break;
    case 3: 
      remind = 1;
      break;
    default:
      remind = 1.5;
  }
  let remindAmountofTimes = Math.floor(difference / remind);
  let drinkAmount = (time.waterAmount * 1000) / remindAmountofTimes;





