   if (secondDigits.length === 1) {
     second = "0" + secondDigits
   }
   else {
     second = secondDigits
   }

   let digitSplit = second.split('');
   let secs = digitSplit.map(Number)
   let sec1 = secs[0]
   let sec2 = secs[1]






  return (
    <div className="App">
      <h1 className="drink-heading">Drink up!</h1>
      <div className="goal">
        <div className="icon"><FontAwesomeIcon icon={faBullseye} /> </div>
        <h2 className="sub-heading">Your Goal</h2>
        <p className="test-text">
          To drink <span className="drink-amount">{time.waterAmount}</span> litres in <span className="drink-amount">difference</span> hours.</p>
        <div className="icon"><FontAwesomeIcon icon={faTrophy} /></div>
        <h2 className="sub-heading">How to achieve</h2>
        <p className="test-text">Drink <span className="drink-amount">drinkAmount</span>  mls every time the countdown reaches 0.
          <br />Press the "I've drank water" button to reset the time<br />
        </p>
      </div>
      {/* <h1>Time now is {currentTime}</h1> */}
      <div className="timer">
      {seconds < 10 ? <div>{minutes.minTotal}:0{seconds.secTotal}</div> : <div>Countdown: {minutes.minTotal}:{seconds.secTotal}</div>} 
      </div>
      {/* <WaterGlass /> */}
      <button className="reset-button">I drank water!! </button>
      <Link to="/"><button>Reset</button></Link>
      {currentHour > time.endValue ? <p>Stop drinking water! You can reset again tomorrow</p> : <p>Keep drinking!!</p>}

    </div>
  )
}

