import React from 'react';
import SelectBox from 'devextreme-react/select-box';
import ArrayStore from 'devextreme/data/array_store';
import { startTimes, endTimes } from '../times'

class App extends React.Component {
  constructor(props) {
    super(props);
    this.start = startTimes;
    this.end = endTimes;
    this.data = new ArrayStore({
      data: this.time,
      key: 'id',
    });
    this.state = {
      value: this.startTimes[0].time,
    };
    this.onValueChanged = this.onValueChanged.bind(this);
    console.log(this.data)
  }


  onValueChanged(e) {
    this.setState({
      value: e.value,
    });
  }

  render() {
    return (
      <div>
        <div className="dx-fieldset">
          <div className="dx-fieldset-header">Event Handling</div>
          <div className="dx-field">
            <div className="dx-field-label">Product</div>
            <div className="dx-field-value">
              <SelectBox 
                items={this.startTimes}
                placeholder="Choose Product"
                showClearButton={true}
                value={this.state.value}
                onValueChanged={this.onValueChanged} />
            </div>
          </div>
          <div className="current-value">
        Selected product is <span>{this.state.value}</span>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
