import { React, useState } from 'react';
import Dropdown from '../components/Dropdown';
import Checkbox from '../components/Checkbox';
import Slider from '../components/Slider';
import { startTimes, endTimes } from '../times';


function Questions(props) {

  const [sliderValue, setSliderValue] = useState(2)

  console.log(startTimes[0].time)

  function handleStartSelect() {
    console.log("Selected")
  }

  const makeStartTimes = startTimes.map(startTime => {
    return (
      <option key={startTime.id}>{startTime.time}</option>
    )
  })

  const makeEndTimes = endTimes.map(endTime => {
    return (
      <option key={endTime.id}>{endTime.time}</option>
    )
  })

  function handleSlider(event) {
    setSliderValue(event.target.value)
  }



  return (
    <>
    <h2>Start drinking water at...</h2>
    <Dropdown options={makeStartTimes} change={handleStartSelect}/>

    <h2>Stop drinking water at ...</h2>
    <Dropdown options={makeEndTimes}/>
    <h2>What days would you like to be reminded on?</h2>
    <Checkbox />
    <Slider sliderChange={handleSlider} sliderValue={sliderValue}/>
    <button className="get-started" onClick={props.drinkClick}>Start drinking!</button>
    </>
  )
}

export default Questions;
