import { React, useState, useEffect } from 'react';
import { Link } from "react-router-dom"
import WaterGlass from '../components/WaterGlass';

function WaterPage() {

  const [time, setTime] = useState([]);
  const [counter, setCounter] = useState(60);
  const [minutes, setMinutes] = useState();

  let today = new Date();
  let currentHour = today.getHours();
  let difference = time.endValue - time.startValue;
  let remind;
  switch (time.remindValue) {
    case 1:
      remind = 0.5;
      break;
    case 2: 
      remind = 0.75;
      break;
    case 3: 
      remind = 1;
      break;
    default:
      remind = 1.5;
  }

  useEffect(() => {
    const time = JSON.parse(localStorage.getItem('time'));
    if (time) {
     setTime(time);
     setMinutes(time.remindAmount - 1)
    }
  }, []);


  let remindAmountofTimes = Math.floor(difference / remind)
  let drinkAmount = parseInt(time.waterAmount)
  console.log(drinkAmount)

  useEffect(() => {
    counter > 0 && setTimeout(() => setCounter(counter - 1), 1000);
    if(counter === 0) {
      setTime(prevTime => ({
        ...time,
        timeAmount: (prevTime - 1)
      }))
      setCounter(59)
    }
    if (minutes === 0) {
      setMinutes(30)
    }

  }, [counter]);



  return (
    <>
      <h1 className="drink-heading">Drink up!</h1>
      <h1 className="test-text">
        To achieve your goal {time.waterAmount} in {difference} hours, you must drink 
        <br/> {drinkAmount} <br/>
        every time the countdown reaches 0.       
      </h1>
      {/* <h1>Time now is {currentTime}</h1> */}
      {currentHour > time.endValue ? <p>Stop drinking water! You can reset again tomorrow</p> : <p>Keep drinking!!</p>}
      <div className="timer">
      {counter < 10 ? <div>Countdown: {minutes}:0{counter}</div> : <div>Countdown: {minutes}:{counter}</div>} 
      </div>
      {/* <WaterGlass /> */}
      <button className="reset-button">I drank water!! </button>
      <Link to="/"><button>Reset</button></Link>
    </>
  )
}

export default WaterPage;


