import React from 'react';
import Dropdown from '../components/Dropdown';
import Checkbox from '../components/Checkbox';
import { startTimes, endTimes } from '../times';


function Questions(props) {

  console.log(startTimes.time)

  function handleStartSelect() {
    console.log("Selected")
  }


  return (
    <>
    <h2>Start drinking water at...</h2>
    <Dropdown options={startTimes.time} change={handleStartSelect}/>
    <h2>Stop drinking water at ...</h2>
    <Dropdown />
    <h2>What days would you like to be reminded on?</h2>
    <Checkbox />
    <button className="get-started" onClick={props.drinkClick}>Start drinking!</button>
    </>
  )
}

export default Questions;

