import { React, useState } from 'react';
import './styles/App.css';
import StartButton from './components/StartButton';
import Questions from './components/Questions';
import WaterPage from './components/WaterPage';

function App() {
  const [start, setStart] = useState("start")
  const [startDrink, setStartDrink] = useState("start")

  function handleClick() {
    setStart(true)
  }

  function handleStartClick() {
    setStartDrink(true)
  }

  return (
    <div className="App">
      <header className="header">Drink more!</header>
      {start === "start" ? <Questions drinkClick={handleStartClick}/> : <StartButton startClick={handleClick}/>}
      {startDrink ? <WaterPage /> : null}
    </div>
  );
}

export default App;
