  }

  const makeStartTime = startTimes.map(startTime => {
    return (
      <Dropdown options={startTime.time} change={handleStartSelect}/>
    )
  })


  return (
    <>
    <h2>Start drinking water at...</h2>
    <Dropdown options={startTimes.map(startTime => { return ( startTime.time)})} change={handleStartSelect}/>
